mapper.update:

update order_detail
set detail_order = #{detailOrder,jdbcType=VARCHAR},
  detail_goods = #{detailGoods,jdbcType=INTEGER},
  detail_price = #{detailPrice,jdbcType=REAL},
  detail_num = #{detailNum,jdbcType=INTEGER}
where detail_id = #{detailId,jdbcType=INTEGER}

mapper.select:

select detail_id, detail_order, detail_goods, detail_price, detail_num
from order_detail
where detail_id = #{detailId,jdbcType=INTEGER}

select detail_id
from order_detail
where detail_order = #{detailOrder,jdbcType=VARCHAR}
